package javaprojectfirstpackage;

public class Exe {
	int third_Angle(int a_1, int a_2) {
		// int sum=a_1+a_2;
		int Third_angle = 180 - (a_1 + a_2);
		return Third_angle;

	}

	int sumOfThreeNumbers(int a, int b, int c) {
		int sum31 = a + b + c;
		return sum31;
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Exe obj = new Exe();
		obj.third_Angle(100, 30);
		obj.sumOfThreeNumbers(3, 3, 3);
		int eight = 0Xabcdf;
		System.out.println(eight);

	}

}
